$(function() {
    $(".draggable").draggable({
        helper: "clone",
        drag: function(e, t) {
            $(".droppable").droppable({
                accept: ".draggable",
                hoverClass: "drop-hover",
                drop: function(e, t) {
                    droppableDiv = $(e.target).attr("id");
                    searchId = t.draggable.attr("id");
                    panelid = $(e.target).children(".panel-heading").attr("id", searchId);
                    for (i = 0; i < chartLister.length; i++){
                      var chartname = chartLister[i];
                      if ($(e.target).children(".panel-heading").hasClass(chartLister[i])) {
                        var data = {"graph_type" : chartname, "bodyid" : droppableDiv}
                        $.ajax({
                            type: "POST",
                            url: "/users/" + gon.userid + "/searches/" + searchId + "/show_chart",
                            data: data
                        })
                      }
                    }
                }
            })
        }
    })
});
